import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import { robots } from './robots';


class App extends Component {
	constructor() {
		super()
		this.state = {
			robots: robots,
			searchfield:''
		}
	}

//use this syntax for non-pre-built react functions such as render and constructor, 
//otherwise, the input->event->this doesnt have a state
	onSearchChange = (event) => { 
		const filterRobot = this.state.robots.filter(robot => {
			return robot.name.toLowerCase().includes(this.searchfield.toLowerCase())
		})
		console.log(filterRobot);
	}

	render() {
		return (
			<div className='tc'>
				<h1>RoboFriends</h1>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robots={this.state.robots} />
			</div>

		);

	}

}

export default App;
